mapper.select:
SELECT ym.`month` as contract_month, IFNULL(tm.total,0) contract_amount, tm.supplier_id, tm.supplier_name FROM rep_year_month ym LEFT JOIN ( SELECT supplier_id, supplier_name, IFNULL(SUM(contract_amount), 0) total, contract_month, apply_no, contract_no FROM v_scm_purchase_contract
and contract_year = #{contractYear} and supplier_id = #{supplierId}
GROUP BY contract_month, supplier_id ) tm ON ym.NO = tm.contract_month ORDER BY ym.no

SELECT yq.`quarter` as contract_quarter, IFNULL(tm.total,0) contract_amount, tm.supplier_id, tm.supplier_name FROM rep_year_quarter yq LEFT JOIN ( SELECT supplier_id, supplier_name, IFNULL(SUM(contract_amount), 0) total, contract_quarter, apply_no, contract_no FROM v_scm_purchase_contract
and contract_year = #{contractYear} and supplier_id = #{supplierId}
GROUP BY contract_quarter, supplier_id ) tm ON yq.NO = tm.contract_quarter ORDER BY yq.no
