mapper.select:
select ym.month contract_month, material_id, material_name, purchase_num, arrive_num, return_num, in_warehouse_num, purchase_amount, return_amount FROM rep_year_month ym LEFT JOIN ( SELECT pc.contract_month, pc.material_id, m.NAME AS material_name, IFNULL( pc.purchase_num, 0 ) purchase_num, IFNULL( pa.arrive_num, 0 ) arrive_num, IFNULL( pr.return_num, 0 ) return_num, ifnull( arrive_num - return_num, 0 ) in_warehouse_num, IFNULL( pc.purchase_amount, 0 ) purchase_amount, IFNULL( pa.arrive_amount, 0 ) arrive_amount, IFNULL( pr.return_amount, 0 ) return_amount FROM ( SELECT d.material_id, ifnull( sum( d.quantity ), 0 ) purchase_num, ifnull( sum( d.quantity * d.price ), 0 ) purchase_amount, MONTH ( c.sign_date ) AS contract_month FROM scm_purchase_contract c INNER JOIN scm_purchase_contract_detail d ON d.contract_id = c.id
c.audit_status = '1' and c.supplier_id = #{supplierId} and date_format( c.sign_date, '%Y' ) = #{contractYear}
GROUP BY contract_month, d.material_id ) pc LEFT JOIN ( SELECT d.material_id, ifnull( sum( d.arrive_quantity ), 0 ) arrive_num, MONTH ( a.arrive_date ) AS arrive_month, sum( d.arrive_quantity * d.price ) AS arrive_amount FROM scm_purchase_arrive a INNER JOIN scm_purchase_arrive_detail d ON d.arrive_id = a.id
and a.supplier_id = #{supplierId} and date_format( a.arrive_date, '%Y' ) = #{contractYear}
GROUP BY arrive_month, d.material_id ) pa ON pa.material_id = pc.material_id AND pa.arrive_month = pc.contract_month LEFT JOIN ( SELECT d.material_id, ifnull( sum( d.return_quantity ), 0 ) return_num, ifnull( sum( d.return_quantity * d.price ), 0 ) return_amount, MONTH ( r.return_date ) AS return_month FROM scm_purchase_return r INNER JOIN scm_purchase_return_detail d ON d.return_id = r.id
and r.supplier_id = #{supplierId} and date_format( r.return_date, '%Y' ) = #{contractYear}
GROUP BY return_month, d.material_id ) pr ON pr.material_id = pc.material_id AND pr.return_month = pc.contract_month LEFT JOIN wms_material_info m ON m.id = pc.material_id
and m.name like concat('%', #{materialName}, '%')
) tm ON tm.contract_month = ym.NO

select yq.quarter contract_quarter, material_id, material_name, purchase_num, arrive_num, return_num, in_warehouse_num, purchase_amount, return_amount FROM rep_year_quarter yq LEFT JOIN ( SELECT pc.contract_quarter, pc.material_id, m.NAME AS material_name, IFNULL( pc.purchase_num, 0 ) purchase_num, IFNULL( pa.arrive_num, 0 ) arrive_num, IFNULL( pr.return_num, 0 ) return_num, ifnull( arrive_num - return_num, 0 ) in_warehouse_num, IFNULL( pc.purchase_amount, 0 ) purchase_amount, IFNULL( pa.arrive_amount, 0 ) arrive_amount, IFNULL( pr.return_amount, 0 ) return_amount FROM ( SELECT d.material_id, ifnull( sum( d.quantity ), 0 ) purchase_num, ifnull( sum( d.quantity * d.price ), 0 ) purchase_amount, QUARTER ( c.sign_date ) AS contract_quarter FROM scm_purchase_contract c INNER JOIN scm_purchase_contract_detail d ON d.contract_id = c.id
c.audit_status = '1' and c.supplier_id = #{supplierId} and date_format( c.sign_date, '%Y' ) = #{contractYear}
GROUP BY contract_quarter, d.material_id ) pc LEFT JOIN ( SELECT d.material_id, ifnull( sum( d.arrive_quantity ), 0 ) arrive_num, QUARTER ( a.arrive_date ) AS arrive_quarter, sum( d.arrive_quantity * d.price ) AS arrive_amount FROM scm_purchase_arrive a INNER JOIN scm_purchase_arrive_detail d ON d.arrive_id = a.id
and a.supplier_id = #{supplierId} and date_format( a.arrive_date, '%Y' ) = #{contractYear}
GROUP BY arrive_quarter, d.material_id ) pa ON pa.material_id = pc.material_id AND pa.arrive_quarter = pc.contract_quarter LEFT JOIN ( SELECT d.material_id, ifnull( sum( d.return_quantity ), 0 ) return_num, ifnull( sum( d.return_quantity * d.price ), 0 ) return_amount, QUARTER ( r.return_date ) AS return_quarter FROM scm_purchase_return r INNER JOIN scm_purchase_return_detail d ON d.return_id = r.id
and r.supplier_id = #{supplierId} and date_format( r.return_date, '%Y' ) = #{contractYear}
GROUP BY return_quarter, d.material_id ) pr ON pr.material_id = pc.material_id AND pr.return_quarter = pc.contract_quarter LEFT JOIN wms_material_info m ON m.id = pc.material_id
and m.name like concat('%', #{materialName}, '%')
) tm ON tm.contract_quarter = yq.NO
